import { Component } from '@angular/core';
import { Cell } from '../models/Cell';
import { CellService } from '../services/Cell.service';
import { Estado } from '../models/Estado';


@Component({
  selector: 'app-board',
  templateUrl: './board.component.html',
  styleUrls: ['./board.component.css']
})
export class BoardComponent {
  
  board: Cell[][] = [];
  gameState: Estado = Estado.playing;

  rows: number;
  columns: number;
  numberOfMines: number;
  celdas: any;
  difficulty: string;

  constructor(private cellService: CellService){
    this.rows= 5;
    this.columns = 5;
    this.numberOfMines = 0;
    this.difficulty = "Easy"
    this.celdas = {cellsOpened: 0, cells: this.rows * this.columns, numberMines: this.numberOfMines};
    this.changeDifficulty();
  }

  checkCell(row: number, column: number){
    this.cellService.abrirCelda(this.board, row, column, this.rows, this.columns, this.celdas);
    this.gameState = this.cellService.cambiarEstado(this.board, row, column, this.celdas, this.numberOfMines, this.gameState);
  }

  reiniciar(){
    this.board = this.cellService.buildBoard(this.rows, this.columns, this.numberOfMines);
    this.gameState = Estado.playing;
    this.celdas = {cellsOpened: 0, cells: this.rows * this.columns, numberMines: this.numberOfMines};
  }

  getDifficultys() : string[]{
    return this.cellService.getDifficulty();
  }

  changeDifficulty(){
    let changes: [number, number, number] = this.cellService.Dificultad(this.difficulty);
    this.rows = changes[0];
    this.columns = changes[1];
    this.numberOfMines = changes[2]; 
    this.reiniciar();
  }

}

<p>Escoge la dificultad</p>
<select (change)="changeDifficulty()"
        [(ngModel)]="difficulty"
        class="et">
    <option 
        *ngFor="let mode of this.getDifficultys()" 
        value="{{mode}}">
        {{mode}}
    </option>
</select>

<p>Numero de minas: {{numberOfMines}}</p>

<div class="reset">
    <button (click)="reset()" *ngIf="gameState == 0 || gameState == 2">
        Reintentar
    </button>
</div>

<div class="Win">
    <h3 *ngIf="gameState == 2" >
        Ganaste. Juego Terminado
    </h3>
</div>

<div class="Lose">
    <h3 *ngIf="gameState == 0" >
        Perdiste. Juego Terminado
    </h3>
</div>

<div *ngIf="gameState == 1 || gameState == 0 || gameState == 2">
    <table class="Game">
        <tr *ngFor="let rows of board; index as y;">
            <td *ngFor="let cell of rows; index as x;" 
            (click)="gameState == 1 && checkCell(y, x)" 
            (contextmenu)="gameState == 1" 
            [ngClass]="{'closedCell': cell.status != 'open' && !(gameState == 0 && cell.mina) }"
            >
                <div 
                *ngIf="cell.status =='open' && cell.minasAlrededor != 0" 
                class="celdas"
                >
                    {{cell.minasAlrededor}}
                </div>

                <div *ngIf="cell.status == 'mine'" class="mine">
                    💣
                </div>
                
            </td>
        </tr>
    </table>
</div>
