import { Component } from '@angular/core';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html'
})
export class AppComponent {
  title = 'app';
  difficulty: string = ''; // Variable para almacenar la dificultad seleccionada

  // Método para manejar el evento de selección de dificultad
  onDifficultySelected(dificultad: string) {
    this.difficulty = dificultad; // Almacena la dificultad seleccionada
  }
}
<body>
  <app-nav-menu></app-nav-menu>
  <main class="container">
    <router-outlet></router-outlet>
  </main>
</body>

<app-home (seleccionarDificultad)="onDifficultySelected($event)"></app-home>
<app-board [Dificultad]="difficulty"></app-board>
